    public static void openSoftwareKeyboard(View view)
    {
        InputMethodManager imm = (InputMethodManager) view.getContext().getSystemService(Context.INPUT_METHOD_SERVICE);
        imm.showSoftInput(view, InputMethodManager.SHOW_FORCED);
    }

    public static void closeSoftwareKeyboard(View view)
    {
        InputMethodManager imm = (InputMethodManager) view.getContext().getSystemService(Context.INPUT_METHOD_SERVICE);
        imm.hideSoftInputFromWindow(view.getWindowToken(), 0);
    }

    public static void showMessage(Context context,
                                   @StringRes int stringRes,
                                   boolean isLong)
    {
        Resources resources = context.getResources();
        final String message = resources.getString(stringRes);
        Toast.makeText(context, message, isLong ? Toast.LENGTH_LONG : Toast.LENGTH_SHORT).show();
    }

    public static void showMessage(Context context,
                                   String message,
                                   boolean isLong)
    {
        Toast.makeText(context, message, isLong ? Toast.LENGTH_LONG : Toast.LENGTH_SHORT).show();
    }

    public static boolean isNetAvailable(Context context)
    {
        boolean isLorAbove = Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP;

        if(isLorAbove)
        {
            return isNetAvailableLollipop(context);
        }

        ConnectivityManager connectivityManager = (ConnectivityManager) context.getSystemService(
                Context.CONNECTIVITY_SERVICE);

        if(connectivityManager != null)
        {
            NetworkInfo[] infos = connectivityManager.getAllNetworkInfo();

            if(infos != null)
            {
                for(NetworkInfo info : infos)
                {
                    if(info.isConnectedOrConnecting())
                    {
                        return true;
                    }
                }
            }
        }

        return false;
    }

    /**
     * Syntactic sugar method for code clarity.
     * Example:
     * ... if(Utilities.netIsDown(context)) ...
     * Or even better with a static import:
     * ... if(netIsDown(context)) ...
     */
    public static boolean netIsDown(Context context)
    {
        return !isNetAvailable(context);
    }

    @TargetApi(Build.VERSION_CODES.LOLLIPOP)
    private static boolean isNetAvailableLollipop(Context context)
    {
        ConnectivityManager connectivityManager = (ConnectivityManager) context.getSystemService(
                Context.CONNECTIVITY_SERVICE);

        if(connectivityManager != null)
        {
            Network[] networks = connectivityManager.getAllNetworks();
            if(networks != null)
            {
                for(Network network : networks)
                {
                    NetworkInfo info = connectivityManager.getNetworkInfo(network);
                    if(info.isConnectedOrConnecting())
                    {
                        return true;
                    }
                }
            }
        }

        return false;
    }

