object Utilities {
    fun openSoftwareKeyboard(view: View) {
        val imm = view.context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager
        imm.showSoftInput(view, InputMethodManager.SHOW_FORCED)
    }

    fun closeSoftwareKeyboard(view: View) {
        val imm = view.context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager
        imm.hideSoftInputFromWindow(view.windowToken, 0)
    }

    fun showMessage(context: Context,
                    @StringRes stringRes: Int,
                    isLong: Boolean) {
        val resources = context.resources
        val message = resources.getString(stringRes)
        Toast.makeText(context, message, if (isLong) Toast.LENGTH_LONG else Toast.LENGTH_SHORT).show()
    }

    fun showMessage(context: Context,
                    message: String,
                    isLong: Boolean) {
        Toast.makeText(context, message, if (isLong) Toast.LENGTH_LONG else Toast.LENGTH_SHORT).show()
    }

    fun isNetAvailable(context: Context): Boolean {
        val isLorAbove = Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP

        if (isLorAbove) {
            return isNetAvailableLollipop(context)
        }

        val connectivityManager = context.getSystemService(
                Context.CONNECTIVITY_SERVICE) as ConnectivityManager

        if (connectivityManager != null) {
            val infos = connectivityManager.allNetworkInfo

            if (infos != null) {
                for (info in infos) {
                    if (info.isConnectedOrConnecting) {
                        return true
                    }
                }
            }
        }

        return false
    }

    /**
     * Syntactic sugar method for code clarity.
     * Example:
     * ... if(Utilities.netIsDown(context)) ...
     * Or even better with a static import:
     * ... if(netIsDown(context)) ...
     */
    fun netIsDown(context: Context): Boolean {
        return !isNetAvailable(context)
    }

    @TargetApi(Build.VERSION_CODES.LOLLIPOP)
    private fun isNetAvailableLollipop(context: Context): Boolean {
        val connectivityManager = context.getSystemService(
                Context.CONNECTIVITY_SERVICE) as ConnectivityManager

        if (connectivityManager != null) {
            val networks = connectivityManager.allNetworks
            if (networks != null) {
                for (network in networks) {
                    val info = connectivityManager.getNetworkInfo(network)
                    if (info.isConnectedOrConnecting) {
                        return true
                    }
                }
            }
        }

        return false
    }

