    fun <T> nullCheck(obj: T?): T {
        if (obj == null) {
            throw NullPointerException("Null object.")
        }
        return obj
    }

    fun <T> nullCheck(obj: T?,
                      message: String?): T {
        if (obj == null) {
            throw NullPointerException(if (message == null) "" else "object == null")
        }
        return obj
    }

    @Throws(NullPointerException::class)
    fun <T : Any> nullElementCheck(collection: List<T>): List<T> {
        if (collection == null) {
            throw NullPointerException()
        }

        for (o in collection) {
            if (o == null) {
                throw NullPointerException()
            }
        }
        return collection
    }

    /**
     * Based on true positions in the SparseBooleanArray, filter out objects from source and returns
     * a list of filtered elements. None of the elements in the source collection can be null.

     * @throws NullPointerException if any of the arguments are null
     */
    @Throws(Exception::class)
    fun <T : Any> sparseBooleanFilteringFromSource(source: List<T>,
                                                   positions: SparseBooleanArray): List<T> {
        Utilities.nullElementCheck(source)
        Utilities.nullCheck(positions)

        val toDelete = ArrayList<T>(positions.size())
        var i = 0
        val size = positions.size()
        while (i < size) {
            toDelete.add(source[positions.keyAt(i)])
            i++
        }
        return toDelete
    }

    @Throws(Exception::class)
    fun containsKey(savedInstanceState: Bundle,
                    key: String): Boolean {
        Utilities.nullCheck(savedInstanceState)
        Utilities.nullCheck(key)
        return savedInstanceState.containsKey(key)
    }

    /**
     * @return the same reference that was passed in as argument
     * *
     * @throws NullPointerException  if the source collection is null
     * *
     * @throws IllegalStateException if the source is empty and non-null
     */
    @Throws(Exception::class)
    fun <T : Any> nonEmpty(source: Collection<T>): Collection<T> {
        nullCheck(source)
        if (source.isEmpty()) {
            throw IllegalStateException("collection is empty")
        }
        return source
    }

    @Throws(Exception::class)
    fun notEmpty(email: String) {
        if (email.isEmpty()) {
            throw IllegalArgumentException("argument == empty")
        }
    }
}
